ver\r\nknife client list\r\nknife cookbook list\r\n# 为工作站安装并配置Knife Client\r\nknife client create chef-workstation -d -a -f ~/.chef/chef-workstation.pem\r\n# 安装完成后检查并确认以下端口：\r\n# Chef Server - 4000\r\n# Chef Server WebUI - 4040\r\n# CouchDB - 5984\r\n# RabbitMQ - 5672\r\n# Chef Solr - 8983\r\n\r\n# 在浏览器访问http://chef-server:4040 （正常访问需要在本地电脑的hosts中添加“10.6.1.170 chef-server”）\r\n# 账号：admin\r\n# 密码：chef-server\r\n\r\n	* chef-workstation\r\n\r\n# 更新现有系统\r\napt-get update\r\n\r\n\r\n# 安装Ruby与其它依赖包\r\napt-get install -y ruby ruby-dev libopenssl-ruby rdoc ri irb build-essential wget ssl-cert curl\r\napt-get -y upgrade\r\n\r\n# 安装RubyGems\r\ncd /tmp\r\nwget http://production.cf.rubygems.org/rubygems/rubygems-1.8.10.tgz\r\ntar zxf rubygems-1.8.10.tgz\r\ncd rubygems-1.8.10\r\nruby setup.rb --no-format-executable\r\n\r\n# 安装Chef Gem\r\ngem install chef --no-ri --no-rdoc\r\n\r\n# 安装Git\r\napt-get -y install git-core\r\ngit --version\r\ngit version 1.7.1\r\n\r\n# 创建Chef Repository\r\n# 备注：Chef的大部分配置工作都是在Workstaion中的Chef Repository中完成的，不同的Chef Repository可以管理不同的Chef Server。\r\ngit clone git://github.com/opscode/chef-repo.git /opt/chef-local\r\ncd /opt/chef-local/\r\n\r\n# 创建配置文件夹\r\n# 传输pem认证文件到Workstation\r\nmkdir -p .chef\r\nscp root@chef-server:/root/.chef/chef-workstation.pem .chef/\r\nscp root@chef-server:/root/.chef/validation.pem .chef/\r\nknife configure\r\n\r\n# 验证配置是否正确\r\nntpdate chef-server\r\n\r\n# 确认Knife工具能否连接到Chef Server\r\nknife client list\r\nknife client show chef-validator\r\n\r\n	* chef-client\r\n\r\n# 编辑hosts\r\nvi /etc/hosts\r\n\r\n# 与chef-server同步时间\r\nntpdate chef-server\r\n\r\n# Boostrap可以用来将目标节点初始化为一个Client\r\n# 下面对chef-client-1进行初始化\r\nknife bootstrap 10.102.2.2\r\n\r\n# 验证chef-client-1是否已经注册\r\nknife client list\r\n','#!/bin/bash\r\n# This script for Debian/Ubuntu systems.\r\n\r\n# Difine var\r\n\r\nEXPECT=/usr/bin/expect\r\nINSTALL_DIR=/root/chef_install\r\nCHEF_REPO_DIR=/opt/chef/chef-repo\r\nCHEF_C_PACKAGE=chef_11.14.6-1_amd64.deb\r\nCHEF_S_PACKAGE=chef-server_11.1.4-1_amd64.deb\r\nCHEF_SERVER_HOST=$1\r\nSCR_FILE=\"/etc/chef-server/*.pem\"\r\nDEST_DIR=\"$INSTALL_DIR\"\r\nINSTALL_LOG=/tmp/chef_log.log\r\nPASSWORD=\"KbAGs6GOQyxI52\"\r\n\r\n# Install expect and update system\r\napt-get update\r\napt-get install -y expect\r\napt-get upgrade\r\n\r\nCheck_Run(){\r\n	[ ! -d $INSTALL_DIR ] && mkdir -p $INSTALL_DIR\r\n	[ ! -d $CHEF_REPO_DIR ] && mkdirp -p $CHEF_REPO_DIR\r\n	if [ -f $CHEF_S_PACKAGE ]; then\r\n		echo \"`date` $CHEF_S_PACKAGE: No such file or directory\" >> $INSTALL_LOG\r\n		exit 1\r\n	fi\r\n	if [ -f $CHEF_C_PACKAGE ]; then\r\n		echo \"`date` $CHEF_C_PACKAGE: No such file or directory\" >> $INSTALL_LOG\r\n		exit 1\r\n	fi\r\n	if [ -f $EXPECT ]; then\r\n		echo \"`date` $EXPECT: No such file or directory\" >> $INSTALL_LOG\r\n		exit 1\r\n	fi\r\n}\r\n\r\nexcpect_scp() {\r\n	cat <<-EOF >$INSTALL_DIR/excpect_scp.exp\r\n	#!/usr/bin/expect\r\n	spawn scp root@$CHEF_SERVER_HOST:$SCR_FILE $DEST_DIR\r\n	expect {\r\n	\"*yes/no\" { send \"yes\\r\"; exp_continue}\r\n	\"*password:*\" { send \"$PASSWORD\\r\" }  \r\n	}\r\n	expect eof\r\n	EOF\r\n	chmod 700 $INSTALL_DIR/excpect_scp.exp\r\n}\r\n\r\nset_Passwd(){\r\n	set_password=$(expect -c \"\r\n	spawn passwd root\r\n	expect \\\"*password:*\\\" \r\n	send \\\"$PASSWORD\\r\\\" \r\n	expect \\\"*password:*\\\" \r\n	send \\\"$PASSWORD\\r\\\" \r\n	expect oef\r\n\")\r\n\r\necho $set_password\r\n}\r\n\r\nOn_PasswordAuth(){\r\n	sed -i \"s/PasswordAuthentication.*/PasswordAuthentication yes/g\" /etc/ssh/sshd_config\r\n	service ssh restart\r\n}\r\n\r\nOff_PasswordAuth(){\r\n	sed -i \"s/PasswordAuthentication.*/PasswordAuthentication no/g\" /etc/ssh/sshd_config\r\n	service ssh restart\r\n}\r\n\r\nDown_Package(){\r\n	cd $INSTALL_DIR\r\n	wget https://opscode-omnibus-packages.s3.amazonaws.com/ubuntu/12.04/x86_64/$CHEF_S_PACKAGE\r\n	wget https://opscode-omnibus-packages.s3.amazonaws.com/ubuntu/12.04/x86_64/$CHEF_C_PACKAGE\r\n}\r\n\r\nInstall_server(){\r\n	dpkg -i $CHEF_S_PACKAGE\r\n	chef-server-ctl reconfigure\r\n	\r\n}\r\n\r\nInstall_workstation(){\r\n	apt-get install -y git\r\n	git clone git://github.com/opscode/chef-repo.git $CHEF_REPO_DIR\r\n	dpkg -i $CHEF_C_PACKAGE\r\n	mkdir -p $CHEF_REPO_DIR/.chef\r\n	On_PasswordAuth && excpect_scp\r\n	cp $INSTALL_DIR/{admin.pem,chef-validator.pem} $CHEF_REPO_DIR/.chef\r\n	Config_WorkStation=$(\"expect -c \"\r\n	spawn knife configure --initial\r\n	expect oef\r\n	\")\r\n}\r\n\r\nInstall_client(){\r\n	if [ -f $CHEF_C_PACKAGE ]; then\r\n		dpkg -i $CHEF_C_PACKAGE\r\n	fi\r\n}\r\n'),(1262,1251,'attr','status_id','1','3'),(1263,1252,'relation','relates',NULL,'816'),(1264,1253,'relation','relates',NULL,'794'),(1265,1254,'relation','relates',NULL,'803'),(1266,1255,'relation','relates',NULL,'794'),(1267,1256,'relation','relates',NULL,'831'),(1268,1257,'relation','relates',NULL,'836'),(1269,1258,'relation','relates',NULL,'789'),(1270,1259,'relation','relates',NULL,'836'),(1271,1260,'relation','relates',NULL,'829'),(1272,1261,'relation','relates',NULL,'836'),(1273,1262,'relation','relates',NULL,'830'),(1274,1263,'relation','relates',NULL,'836'),(1275,1264,'relation','relates',NULL,'446'),(1276,1265,'relation','relates',NULL,'836'),(1277,1266,'relation','relates',NULL,'53'),(1278,1267,'relation','relates',NULL,'836'),(1279,1268,'relation','relates','829',NULL),(1280,1269,'relation','relates','836',NULL),(1281,1270,'relation','relates',NULL,'839'),(1282,1271,'relation','relates',NULL,'836'),(1283,1272,'attr','status_id','2','3'),(1284,1273,'relation','relates',NULL,'842'),(1285,1274,'relation','relates',NULL,'836'),(1286,1275,'attr','subject','福州共享盘移交','福州共享盘移交并升级'),(1287,1276,'relation','relates','842',NULL),(1288,1277,'relation','relates','836',NULL),(1289,1278,'relation','relates','839',NULL),(1290,1279,'relation','relates','836',NULL),(1291,1280,'relation','relates',NULL,'832'),(1292,1281,'relation','relates',NULL,'836'),(1293,1282,'attr','subject','将erp从ESXi迁移到openstack中','将mantis从ESXi迁移到openstack中'),(1294,1283,'attr','su