)#switchport access vlan 2 //将1/g17接口划入VLAN2\r\n\r\nconsole(config-if-1/g17)#spanning-tree portfast //启用快速端口，可以加快接口UP速度\r\n\r\nconsole(config-if-1/g17)#no shutdown //启用接口\r\n\r\nh2. 3）接口配置Trunk模式\r\n\r\nconsole(config)#interface ethernet 1/g17\r\n\r\nconsole(config-if-1/g17)#switchport mode trunk //将端口模式改为Trunk模式\r\n\r\nconsole(config-if-1/g17)#switchport trunk allowed vlan add 1,2,5-8 //此Trunk接口只允许VLAN 1,2,5-8通过\r\n\r\n'),(1454,1427,'attr','subject','repos/dryrun/reseller/crm/demo环境从ESXi迁移到OpenStack','repos/exchange环境从ESXi迁移到OpenStack'),(1455,1428,'attr','status_id','1','3'),(1456,1429,'relation','relates',NULL,'902'),(1457,1430,'relation','relates',NULL,'895'),(1458,1431,'relation','relates',NULL,'896'),(1459,1432,'relation','relates',NULL,'895'),(1460,1433,'attr','description','HTTP访问：\r\nhttp://sync-us.foxitsoftware.com/qa/\r\nhttp://sync-cn.foxitsoftware.com/qa/\r\n\r\nFTP地址：sync-us.foxitsoftware.com\r\nFTP地址： sync-cn.foxitsoftware.com\r\nftp用户名 密码\r\nes es@140328\r\nse se@140328\r\nsdk sdk@140328\r\nqa qa@0418\r\njp jp@0804\r\nhr hr@0804\r\n\r\n','HTTP访问：\r\nhttp://sync-us.foxitsoftware.com/qa/\r\nhttp://sync-cn.foxitsoftware.com/qa/\r\n\r\nFTP地址：sync-us.foxitsoftware.com\r\nFTP地址： sync-cn.foxitsoftware.com\r\nftp用户名 密码\r\nes es@140328\r\nse se@140328\r\nsdk sdk@140328\r\nqa qa@0418\r\njp jp@0804\r\nhr hr@0804\r\niis iis@0917\r\n\r\n'),(1461,1434,'attr','description','HTTP访问：\r\nhttp://sync-us.foxitsoftware.com/qa/\r\nhttp://sync-cn.foxitsoftware.com/qa/\r\n\r\nFTP地址：sync-us.foxitsoftware.com\r\nFTP地址： sync-cn.foxitsoftware.com\r\nftp用户名 密码\r\nes es@140328\r\nse se@140328\r\nsdk sdk@140328\r\nqa qa@0418\r\njp jp@0804\r\nhr hr@0804\r\niis iis@0917\r\n\r\n','HTTP访问：\r\nhttp://sync-us.foxitsoftware.com/qa/\r\nhttp://sync-cn.foxitsoftware.com/qa/\r\n\r\nFTP地址：sync-us.foxitsoftware.com\r\nFTP地址： sync-cn.foxitsoftware.com\r\nftp用户名 密码\r\nes es@140328\r\nse se@140328\r\nsdk sdk@140328\r\nqa qa@0418\r\njp jp@0804\r\nhr hr@0804\r\niis iis@0917\r\nproject Project@0917\r